--메인 테이블
create table movie_main(
movie_id int primary key,
movie_title varchar2(500) not null,
movie_sub_title varchar2(200),
movie_poster_path varchar2(1000) not null, 
movie_category varchar2(100) not null,
movie_content varchar2(4000) not null,
movie_rating varchar2(100) not null,
movie_like varchar2(1) default 0,
movie_card_back_path varchar2(1000) not null,
movie_card_video_path varchar2(1000) not null,
movie_actor varchar2(500),
inputdate date
);
values(main_no_seq.nextval,#{movieTitle},#{movieSubTitle},#{moviePosterPath},#{movieCategory},#{movieContent},#{movieRating},#{movieCardBackPath},#{movieCardVideoPath},#{movieActor});
